(function(){sec=function(e,t){this.e=document.getElementsByClassName(e)[0],this.t=t,this.update=(()=>this.e.style.transform="rotate("+6*(new Date)[this.t]()+"deg)")};var e=[new sec("sec","getSeconds"),new sec("hr","getHours"),new sec("min","getMinutes")];setInterval(()=>{e.forEach(e=>e.update()),document.getElementsByClassName("tcc")[0].innerHTML=(new Date).toGMTString()},1e3)})();